import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import io from 'socket.io-client';
import axios from 'axios';

// Components
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import DisasterForm from './components/DisasterForm';
import DisasterList from './components/DisasterList';
import DisasterDetail from './components/DisasterDetail';
import SocialMediaFeed from './components/SocialMediaFeed';
import ResourceMap from './components/ResourceMap';
import VerificationPanel from './components/VerificationPanel';
import Login from './components/Login';

// Context
import { AuthContext } from './context/AuthContext';
import { SocketContext } from './context/SocketContext';

// Styles
import './App.css';

// Mock users for authentication
const MOCK_USERS = {
  'netrunnerX': { username: 'netrunnerX', role: 'admin' },
  'reliefAdmin': { username: 'reliefAdmin', role: 'admin' },
  'fieldWorker': { username: 'fieldWorker', role: 'contributor' },
  'volunteer': { username: 'volunteer', role: 'volunteer' },
  'citizen1': { username: 'citizen1', role: 'citizen' }
};

function App() {
  const [user, setUser] = useState(null);
  const [socket, setSocket] = useState(null);
  const [loading, setLoading] = useState(true);

  // Initialize socket connection
  useEffect(() => {
    const newSocket = io('http://localhost:5000', {
      transports: ['websocket', 'polling']
    });

    newSocket.on('connect', () => {
      console.log('Connected to server');
      setSocket(newSocket);
    });

    newSocket.on('disconnect', () => {
      console.log('Disconnected from server');
    });

    newSocket.on('disaster_created', (data) => {
      console.log('New disaster created:', data);
      // You can add toast notifications here
    });

    newSocket.on('disaster_updated', (data) => {
      console.log('Disaster updated:', data);
    });

    newSocket.on('social_media_updated', (data) => {
      console.log('Social media updated:', data);
    });

    newSocket.on('resource_created', (data) => {
      console.log('Resource created:', data);
    });

    newSocket.on('image_verification_completed', (data) => {
      console.log('Image verification completed:', data);
    });

    return () => {
      newSocket.close();
    };
  }, []);

  // Check for stored user on app load
  useEffect(() => {
    const storedUser = localStorage.getItem('disasterResponseUser');
    if (storedUser) {
      const userData = JSON.parse(storedUser);
      setUser(userData);
    }
    setLoading(false);
  }, []);

  // Login function
  const login = (username) => {
    if (MOCK_USERS[username]) {
      const userData = MOCK_USERS[username];
      setUser(userData);
      localStorage.setItem('disasterResponseUser', JSON.stringify(userData));
      
      // Set default authorization header for all requests
      axios.defaults.headers.common['Authorization'] = `Bearer ${username}`;
      
      return true;
    }
    return false;
  };

  // Logout function
  const logout = () => {
    setUser(null);
    localStorage.removeItem('disasterResponseUser');
    delete axios.defaults.headers.common['Authorization'];
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
        <p>Loading Disaster Response Platform...</p>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="app">
        <Login onLogin={login} users={Object.keys(MOCK_USERS)} />
        <Toaster position="top-right" />
      </div>
    );
  }

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      <SocketContext.Provider value={socket}>
        <Router>
          <div className="app">
            <Header />
            <main className="main-content">
              <Routes>
                <Route path="/" element={<Dashboard />} />
                <Route path="/disasters" element={<DisasterList />} />
                <Route path="/disasters/new" element={<DisasterForm />} />
                <Route path="/disasters/:id" element={<DisasterDetail />} />
                <Route path="/social-media" element={<SocialMediaFeed />} />
                <Route path="/resources" element={<ResourceMap />} />
                <Route path="/verification" element={<VerificationPanel />} />
                <Route path="*" element={<Navigate to="/" replace />} />
              </Routes>
            </main>
            <Toaster position="top-right" />
          </div>
        </Router>
      </SocketContext.Provider>
    </AuthContext.Provider>
  );
}

export default App;
